
public class Convert_Rupee_Into_Paisa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double n=56.50;   
		int a=(int)n;
		double m=(n-a)*100;
		System.out.println("Thanks for purchasing our Product you have to pay:");
		System.out.println("Rs    :"+a);
		System.out.println("paisa :"+(int)m);
		

	}

}
