package Star_Pattern;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int r=9;
        int i = 1;
		for(int l=1;l<=i;l++)
        {
        	System.out.print("^");
        }
        for(int y=i;y<=r-1;y++)
        {
        	System.out.println(y);
        }
	}

}
