package Trash;

public class num_pattern0110 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
    		System.out.print("");
    	
		for(int j=1;j<=i;j++) {
    		System.out.print((j%2)+" ");
    	}
    	System.out.println();
	}
	}

}
