package while_loop;

public class Q12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;                  //Initialization 1
		while (i < 6)                //True            i=1           i=2...............i=6     
		{
			System.out.println("Hello  World");  //Hello World  //Hello World....//Hello World...
			i++;         // i=1,2,3,4,5,!6
		}

	}

}
