package while_loop;

public class Q3_While_Loop_Break_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i=1;i++;
       while(i>=1 && i<=10)
    	   {
    	   if(i==4)
    	   
    	   System.out.println(i+"Succeed");
    	   
       break;
       
	}
       System.out.println("loop Breaked Succesfully");
	}

}
