package written_Constructor5;

public class Q31 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=9;i>=1;i++) {
			for(int j=1;j<i;j++) {
				System.out.print(" ");
			}
			for(int k=i;k<=i-1;k++) {
				System.out.print(k);
			}
			for(int l=9;l>=i;l++) {
				System.out.print(l);
			}
			
		}
		System.out.println();
	}

}
